import React,{useEffect, useState, useRef} from "react";
import {BrowserRouter as Router} from 'react-router-dom';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";


import MainNavigation from '../../navbar/pages/MainNavigation';
import SearchOverlay from "./SearchOverlay";
import Carousel from "./Carousel";


const Home = () => {

  const [movieList, setMovieList]= useState([]);
  
  

  const getMovie = ()=>{
    fetch('https://api.themoviedb.org/3/discover/movie?api_key=d987bb3825166942aa314c4768160995')
    .then(res => res.json())
    .then(json => setMovieList(json.results));
  }

  useEffect(()=>{
    getMovie();
  },[]);


  console.log("Home: "+movieList);


  return (
    <>
    <Router>
      <MainNavigation />
    </Router>
    <SearchOverlay />

    <Carousel movieList={movieList} type={"movie"}/>
    
    </>
)
};

export default Home;
