import React, {useRef, useEffect} from 'react';
import { useSelector} from 'react-redux';
import Slider from "react-slick";
import { TbCurrencyRupee } from "react-icons/tb";
import { useDispatch } from 'react-redux';

import classes from './Home.module.css';
import Wishlistbutton from "../../buttons/Wishlistbutton";
import Cartbutton from "../../buttons/Cartbutton";
import {movieDetailsActions} from '../../store/index';
import DetailsModal from "./DetailsModal";


function Carousel({movieList}) {
    
    const dispatch = useDispatch();
    const showMovieDetails = useSelector(state => state.movieDetails.showMovieDetails);
    console.log("showMovieDetails: "+showMovieDetails);

    const dialog = useRef();
    useEffect(() => {
    if (showMovieDetails && dialog.current) {
      dialog.current.open();
    }
  }, [showMovieDetails]);

    const settings = {
        dots: false,
        lazyLoad: true,
        infinite: true,
        speed: 500,
        slidesToShow: 5,
        slidesToScroll: 1
      };

      const getColor = (vote) =>{
        if(vote >= 8)
          return 'green';
        else if(vote>=5 && vote<8)
          return 'orange';
        else
          return 'red';
      }
      const openModal = (event)=>{
        event.preventDefault();
        dispatch(movieDetailsActions.openModal());
      };

  return (
    <>
    
    <div className={classes["container-carousel"]}>
      <div className={classes.overall}>
      <Slider {...settings}>  
      {
      movieList.map((movie)=>(
        <>
        <DetailsModal ref={dialog}/>
        <div className={classes.movie} key={movie.id}>
          
          <img src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`} alt="" title={movie.title} className={classes.image} />

          <div className={classes["movie-info"]}>
            <h3 className={classes.title}>{movie.title}</h3> 
            <span className={classes[`${getColor(movie.vote_average)}`]}>{(movie.vote_average).toFixed(1)}</span>
          </div>

          <div className={classes.overview}>
            <div className={classes.text}>
              {movie.overview}
            </div>
            <span style={{fontSize : "12px",fontFamily: '"Times New Roman", Times, serif'}}>
            <a href="#" onClick={openModal}>Know more</a>
            </span>
            <hr></hr>
            <span style={{float:"left",marginTop: "5px"}}> 
              <TbCurrencyRupee className={classes.currency}></TbCurrencyRupee> 
              <span className={classes["currency-value"]}>{(100.00).toFixed(2)}</span>
            </span>
            <div style={{float:"right"}}>
            <button className={classes["card-button"]}>
             <Wishlistbutton />
            </button>
            <button className={classes["card-button"]}>
              <Cartbutton />
            </button>
            </div>
          </div>
           
        </div>
        </>
      )
    )
    }
    </Slider>
      </div>
    </div>
    </>
  )
};

export default Carousel;
