import React, { useState } from 'react';

import classes from './Login.module.css';

const Login = () => {
const [loginValues, setLoginValues] = useState({
    email : '',
    password : '',
    pwd_show: true
});

const [signupValues, setSignupValues] = useState({
    name: '',
    email : '',
    password : '',
    confirm_password: '',
    pwd_show: true,
    conf_pwd_show: true
});

const [didEdit, setDidEdit] = useState({
    login:{
        email : false
    },
    signup:{
        name: false,
        email : false
    }
});

// const [hidePassword, setHidePassword]= useState(true);

const emailLoginIsInvalid = didEdit.login.email && !loginValues.email.includes('@');
const nameSignupIsInvalid = didEdit.signup.name && !signupValues.name.includes("^(?:((([^0-9_!¡?÷?¿/\\+=@#$%ˆ&*(){}|~<>;:[\]'’,\-.\s])){1,}(['’,\-\.]){0,1}){2,}(([^0-9_!¡?÷?¿/\\+=@#$%ˆ&*(){}|~<>;:[\]'’,\-. ]))*(([ ]+){0,1}(((([^0-9_!¡?÷?¿/\\+=@#$%ˆ&*(){}|~<>;:[\]'’,\-\.\s])){1,})(['’\-,\.]){0,1}){2,}((([^0-9_!¡?÷?¿/\\+=@#$%ˆ&*(){}|~<>;:[\]'’,\-\.\s])){2,})?)*)$");
const emailSignupIsInvalid = didEdit.signup.email && !signupValues.email.includes('@');

function handleSubmit(event)
{
    event.preventDefault();
    var buttonName = event.nativeEvent.submitter.name;
    if (buttonName === 'login')
    {
        console.log(loginValues);
        setLoginValues({
            email : '',
            password : '',
            pwd_show: true
        });
        setDidEdit({
            email : false,
            password : false
        });
    }
    else if (buttonName === 'signup')
    {
        console.log(signupValues);
        setSignupValues({
            name: '',
            email : '',
            password : '',
            confirm_password: '',
            pwd_show: true,
            conf_pwd_show: true
        });
        
    } 
}

function handleLoginChange(identifier, value)
{
    setLoginValues(prevValues => ({
        ...prevValues,
        [identifier]: value
    }));

    setDidEdit(prevEdit => ({
        ...prevEdit,
        [identifier]:false
    }))
}

function handleLoginBlur(identifier)
{
    setDidEdit(prevEdit => ({
        ...prevEdit,
        [identifier]:true
    }))
}

function handleShowPassword(identifier)
{   
    setLoginValues(prevValues => {
        var showpwd = !prevValues.pwd_show;
        return({
            ...prevValues,
            ['pwd_show']: showpwd
        })
        
    })
}

    return (
        <div className={classes.container}>
        <input type="checkbox" id={classes.flip} />
        <div className={classes.cover}>
            <div className={classes.front}>
                <img src="logo.jpg" alt="" />
            </div>
            
        </div>
        <form name="login" onSubmit={handleSubmit} >
            <div className={classes["form-content"]}>
                <div className={classes["login-form"]}>
                    <div className={classes.title}>Login</div>
                    <div className={classes["input-boxes"]}>
                        <div className={classes["input-box"]}>
                            <i className={`fas fa-envelope`}></i>
                            <input 
                            type="text" 
                            placeholder="Enter you email" 
                            onBlur={() => {handleLoginBlur('email')}}
                            onChange={(event)=>{handleLoginChange('email',event.target.value)}} 
                            value={loginValues.email} />
                            
                        </div>
                        {
                            emailLoginIsInvalid && (<div className={classes["control-error"]}>
                                <p>Please enter valid email address</p>
                                </div>)
                        }
                        <div className={classes["input-box"]}>
                            <i className={`fas fa-lock`}></i>
                            <input 
                            type={loginValues.pwd_show ? "password" : "text"} 
                            placeholder="Enter you password" 
                            onChange={(event)=>{handleLoginChange('password',event.target.value)}} 
                            value={loginValues.password} />
                            
                            <a 
                            href='#'
                            onClick={()=>{handleShowPassword('login')}}
                            >
                                <span
                                className={loginValues.pwd_show ? `fas fa-eye` : `fas fa-eye-slash`}
                                />
                            </a>
                        </div>
                        
                        <div className={`${classes.button} ${classes["input-box"]}`}>
                            
                            <input type="submit" name="login" value="Login"/>
                            
                        </div>
                        <div className={`${classes.text} ${classes["login-text"]}`}>Don't have an account? <label htmlFor={classes.flip}>Signup now</label></div>
                    </div>
                </div>
                <div className={classes["signup-form"]}>
                    <div className={classes.title}>Signup</div>
                    <div className={classes["input-boxes"]}>
                        <div className={classes["input-box"]}>
                            <i className="fas fa-user"></i>
                            <input type="text" placeholder="Enter you name"  />
                            
                        </div>
                        <div className={classes["input-box"]}>
                            <i className="fas fa-envelope"></i>
                            <input type="text" placeholder="Enter you email"  />
                            
                        </div>
                        <div className={classes["input-box"]}>
                            <i className="fas fa-lock"></i>
                            <input type="password" placeholder="Enter you password"  />
                            
                        </div> 
                        <div className={classes["input-box"]}>
                            <i className="fa fa-lock"></i>
                            <input type="password" placeholder="Re-enter you password"  />
                            
                        </div>
                        
                        <div className={`${classes.button} ${classes["input-box"]}`}>
                            
                            <input type="submit" name="signup" value="Signup" />
                            
                        </div>
                        <div className={`${classes.text} ${classes["sign-up-text"]}`}>Already have an account? <label htmlFor={classes.flip}>Login now</label></div>
                    </div>
                </div>
            </div>

        </form>
    </div>
    );
}

export default Login ;
