import React,{useEffect, useState,useRef} from "react"
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

import classes from './Home.module.css';

const Home = () => {

  const [movieList, setMovieList]= useState([]);
  const [isTruncated, setIsTruncated] = useState(false);
  const overviewRef = useRef(null);

  const settings = {
    dots: false,
    lazyLoad: true,
    infinite: true,
    speed: 500,
    slidesToShow: 5,
    slidesToScroll: 1
  };

  const getMovie = ()=>{
    fetch('https://api.themoviedb.org/3/discover/movie?api_key=d987bb3825166942aa314c4768160995')
    .then(res => res.json())
    .then(json => setMovieList(json.results));
  }

  const getColor = (vote) =>{
    if(vote >= 8)
      return 'green';
    else if(vote>=5 && vote<8)
      return 'orange';
    else
      return 'red';
  }

  useEffect(()=>{
    getMovie();
    const checkOverflow = () => {
      if (overviewRef.current) {
        if (overviewRef.current.scrollHeight > overviewRef.current.clientHeight) {
          setIsTruncated(true);
        } else {
          setIsTruncated(false);
        }
      }
      
    };

    checkOverflow();

    window.addEventListener('resize', checkOverflow);

    return () => {
      window.removeEventListener('resize', checkOverflow);
    };
  },[]);

  console.log(movieList);

  return (
    <div className={classes["container-carousel"]}>
      <div className={classes.overall}>
      <Slider {...settings}>  
      {
      movieList.map((movie)=>(
        <div className={classes.movie}>
          {/* <div className={classes["img-container"]}> */}
            <img src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`} alt="" title={movie.title}/>
          {/* </div> */}

          {/* <div className={classes.description}>
            <p className={classes.title}>{movie.title}</p>
            <p className={classes.overview}>{movie.overview}</p>
            <button className={classes.submit}>Read More</button>
          </div> */}

          <div className={classes["movie-info"]}>
            <h3 className={classes.title}>{movie.title}</h3> 
            <span className={classes[`${getColor(movie.vote_average)}`]}>{(movie.vote_average).toFixed(1)}</span>
          </div>

          <div className={classes.overview} ref={overviewRef}>
            {movie.overview}
            <a href="https://google.com" className={` ${isTruncated ? classes.showMore : ''}`}>... more</a>
          </div>

        </div>
      )
    )
    }
    </Slider>
      </div>
    </div>
)
};

export default Home;
