import React,{ forwardRef, useImperativeHandle, useRef,useState,useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { createPortal } from 'react-dom';

import {movieDetailsActions} from '../../store/index';
import classes from './Home.module.css';


const DetailsModal = forwardRef(({movieid,type}, ref) => {
const dialog = useRef();

useImperativeHandle(ref, ()=>{
  return {
    open()
    {
      dialog.current.showModal();
    }  
  }
});

const [details, setDetails]= useState({});

  const getDetails = ()=>{
    fetch(`https://api.themoviedb.org/3/${type}/${movieid}?api_key=d987bb3825166942aa314c4768160995`)
    .then(res => res.json())
    .then(json => setDetails(json));
  }

    useEffect(()=>{
      if(movieid && type)
      {
        getDetails();
      }
              
    },[movieid, type]);

    console.log(details);

    var genreNames = '';
    if(Object.keys(details).length > 0)
    {
      var genres = details.genres;
      genreNames = genres.map(genre => genre.name).join(', ');
    }
    
    

const dispatch = useDispatch();
const closeModal = (event)=>{
    dispatch(movieDetailsActions.closeModal());
  };

  return createPortal(
    <dialog ref={dialog} className={classes.infoModal}>
        <h2>{details["original_title"]}</h2>
        <h4>Overview</h4>
        <p>{details.overview}</p>
        <h4>Genre(s)</h4>
        <p>{genreNames}</p>
        {/* <table style={{border:"none"}}>
          <tbody>
          <tr>
            <td style={{textAlign: "center"}}>rrrr</td>
            <td style={{textAlign: "center"}}>aaaa</td>
          </tr>
          </tbody>
        </table> */}
        <form method="dialog">
            <button onClick={closeModal}>Close</button>
        </form>
    </dialog>,
    document.getElementById('modal')
  );
});

export default DetailsModal;
