import React from 'react';

import Carousel from "./Carousel";
import Pagination from "./Pagination";
import classes from './Home.module.css';

function CarouselContainer({id,movieList,contentType,currentPage,handlePageChange}) {
  return (
    <div id={id} className={classes["carousel-container"]}>
      <div className={classes["heading-container"]}>
        <h2 className={classes.heading}>Now Playing</h2>
      </div>
      <Carousel movieList={movieList} type={contentType} />
      <Pagination
        currentPage={currentPage.nowPlaying}
        onPageChange={(page) => handlePageChange({id}, page)}
      />
    </div>
  )
};

export default CarouselContainer;
