import React, { useEffect, useState } from "react";
import Carousel from "./Carousel";

const Home = () => {
  const [movieList, setMovieList] = useState([]);
  const [tvList, setTvList] = useState([]);
  const [loader, setLoader] = useState(true);

  const getMovie = () => {
    fetch("https://api.themoviedb.org/3/discover/movie?api_key=d987bb3825166942aa314c4768160995")
      .then((res) => res.json())
      .then((json) => setMovieList(json.results));
  };

  const getTvShows = () => {
    fetch("https://api.themoviedb.org/3/discover/tv?api_key=d987bb3825166942aa314c4768160995")
      .then((res) => res.json())
      .then((json) => setTvList(json.results));
  };

  useEffect(() => {
    getMovie();
    getTvShows();
    setLoader(false);
  }, []);

  return (
    <>
      {loader ? (
        <img src="./loader.svg" style={{ width: "370px", height: "290px" }} />
      ) : (
        <>
          <Carousel movieList={movieList} type={"movie"} modalType={"homemovie"} />
          <Carousel movieList={tvList} type={"tv"} modalType={"hometv"} />
        </>
      )}
    </>
  );
};

export default Home;
