import React,{useEffect, useState} from 'react';
import {motion, Variants} from 'framer-motion';
import { useSelector } from 'react-redux';

import classes from './Home.module.css';

function SearchOverlay() {
    const showOverlay = useSelector(state => state.searchOverlay.showSearchOverlay);
    const showSearchCarousel = useSelector(state => state.searchDetails.showSearchDetails);
    const searchText = useSelector(state => state.searchDetails.searchText);
    const [movieResults, setMovieResults]= useState([]);
    const [tvResults, setTvResults]= useState([]);

  const searchOverlayText = splitStringUsingRegex("Please press Enter!");
  const charVariants = {
    hidden: { opacity: 0, y: 10 }, // Add a slight translation for better visibility
    reveal: { opacity: 1, y: 0 }
  }
  const containerVariants = {
    hidden: {},
    reveal: { transition: { staggerChildren: 0.05 } }
  };

  function splitStringUsingRegex(inputstring) {
    const characters = [];
    const regex = /[\s\S]/gu;
  
    let match;
    while((match = regex.exec(inputstring)) != null)
    {
      characters.push(match[0]);
    }
    
    return characters;
  };

  const getSearchResults = ()=>{
      fetch(`https://api.themoviedb.org/3/search/movie?language=en-US&api_key=d987bb3825166942aa314c4768160995&query=${searchText}`)
      .then(res => res.json())
      .then(json => setMovieResults(json.results));
  
      fetch(`https://api.themoviedb.org/3/search/tv?language=en-US&api_key=d987bb3825166942aa314c4768160995&query=${searchText}`)
      .then(res => res.json())
      .then(json => setTvResults(json.results));
    }
  
    useEffect(()=>{
      getSearchResults();
    },[]);

    useEffect(()=>{
          if((searchText!==null || searchText!==undefined || searchText!=='') && !showSearchCarousel)
          {
            getSearchResults();           
          }
        },[searchText, type]);

    console.log("movieResults: ");
    console.log(movieResults);

    console.log("tvResults: ");
    console.log(tvResults);

  return (
    <div className={`${classes.overlay} ${showOverlay ? classes.active : ""}`}>

      {showSearchCarousel && <motion.h1 initial="hidden" animate="reveal" variants={containerVariants} className={classes["overlay-text"]}>
        {searchOverlayText.map((char,index) => (
          <motion.span key={`${char}-${index}`} transition={{duration:0.5}} variants={charVariants}>{char}</motion.span>
        ))}
      </motion.h1>}
      {
        !showSearchCarousel && <h1 className={classes["overlay-text"]}>{searchText}</h1>
      }
    </div>
  )
};

export default SearchOverlay;
