import React,{useEffect, useState} from "react"
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

import classes from './Home.module.css';

const Home = () => {
  // document.getElementById('root').className='';
  // document.getElementsByTagName('body').className= classes.body;
  
  // document.getElementById("root").classList.add('');
  // document.getElementById("root").style.className = '';

  // useEffect(() => {
  //     // document.body.classList.add(`${classes["body-style"]}`); 
  //     // document.getElementById("root").classList.add(`${classes["container-root"]}`);
  //     //document.body.classList.add(`${classes.body}`);
  //     // document.getElementById("root").classList.remove(`${classes}`);
  // }, []);

  const [movieList, setMovieList]= useState([]);
  const settings = {
    dots: false,
    lazyLoad: true,
    infinite: true,
    speed: 500,
    slidesToShow: 5,
    slidesToScroll: 1
  };

  const getMovie = ()=>{
    fetch('https://api.themoviedb.org/3/discover/movie?api_key=d987bb3825166942aa314c4768160995')
    .then(res => res.json())
    .then(json => setMovieList(json.results));
  }

  useEffect(()=>{
    getMovie();
  },[]);

  console.log(movieList);

  return (
    <div className={classes["container-carousel"]}>
      <div className={classes.overall}>
      <Slider {...settings}>  
      {
      movieList.map((movie)=>(
        <div className={classes.card}>
          <div className={classes["img-container"]}>
            <img src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`} alt=""/>
          </div>

          <div className={classes.description}>
            <p className={classes.title}>{movie.title}</p>
            <p className={classes.overview}>{movie.overview}</p>
            <button className={classes.submit}>Read More</button>
          </div>
        </div>
      )
    )
    }
    </Slider>
      </div>
    </div>
)
};

export default Home;
