import React, { useEffect, useState } from "react";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

import Carousel from "./Carousel";
import classes from "./Home.module.css";

const Home = () => {
  const [nowPlaying, setNowPlaying] = useState([]);
  const [trending, setTrending] = useState([]);
  const [topRated, setTopRated] = useState([]);
  const [upcoming, setUpcoming] = useState([]);
  const [loader, setLoader] = useState(true);

  function checkData(data) {
    return (data.poster_path !== null && data.poster_path !== undefined && data.poster_path !== "") 
        && (data.title !== null && data.title !== undefined && data.title !== "")
        && (data.vote_average !== null && data.vote_average !== undefined && data.vote_average !== "")
        && (data.overview !== null && data.overview !== undefined && data.overview !== "");
  }



  const fetchMovies = async (url, setState) => {
    try {
      const response = await fetch(url);
      const data = await response.json();
      setState(data.results.filter(checkData));
    } catch (error) {
      console.error("Error fetching movies:", error);
    }
  };

  useEffect(() => {
    const apiKey = "d987bb3825166942aa314c4768160995";

    fetchMovies(
      `https://api.themoviedb.org/3/movie/now_playing?language=en-US&page=1&api_key=${apiKey}`,
      setNowPlaying
    );
    fetchMovies(
      `https://api.themoviedb.org/3/trending/movie/week?language=en-US&page=1&api_key=${apiKey}`,
      setTrending
    );
    fetchMovies(
      `https://api.themoviedb.org/3/movie/top_rated?language=en-US&page=1&api_key=${apiKey}`,
      setTopRated
    );
    fetchMovies(
      `https://api.themoviedb.org/3/movie/upcoming?language=en-US&page=1&api_key=${apiKey}`,
      setUpcoming
    );

    setLoader(false);
  }, []);

  return (
    <>
      {loader ? (
        <img src="./loader.svg" style={{ width: "370", height: "290" }} />
      ) : (
        <>
          <div className={classes["carousel-container"]} style={{marginTop:"5rem"}}>
            <div className="heading-container">
              <h2 className="heading">Now Playing</h2>
            </div>
            <Carousel movieList={nowPlaying} type={"movie"} />
          </div>
          <div className={classes["carousel-container"]}>
            <h2 className={classes.heading}>Trending</h2>
            <Carousel movieList={trending} type={"movie"} />
          </div>
          <div className={classes["carousel-container"]}>
            <h2 className={classes.heading}>Top Rated</h2>
            <Carousel movieList={topRated} type={"movie"} />
          </div>
          <div className={classes["carousel-container"]}>
            <h2 className={classes.heading}>Upcoming</h2>
            <Carousel movieList={upcoming} type={"movie"} />
          </div>
        </>
      )}
    </>
  );
};

export default Home;
