import React from "react";
import { motion } from "framer-motion";
import { IoMdCart } from "react-icons/io";
import { MdDeleteForever } from "react-icons/md";
import classes from "./Wishlist.module.css";

const Wishlist = () => {
   const wishlist = JSON.parse(localStorage.getItem('cart')) || [];

  const itemVariants = {
    hidden: { opacity: 0, x: 50 }, // Start with opacity 0 and move from the right
    visible: (i) => ({
      opacity: 1,
      x: 0,
      transition: { delay: i * 0.2, type: "spring", stiffness: 100 },
    }),
  };

  return (
    <div className={classes.wishlist}>
      {wishlist.map((item, index) => (
        <motion.div
          className={classes.wishlistItem}
          key={item.id}
          custom={index} // Pass the index for staggered animation
          initial="hidden"
          animate="visible"
          variants={itemVariants}
        >
          <div className={classes.type}>{item.type.toUpperCase()}</div>
          <div className={classes.title}>{item.title}</div>
          <div className={classes.price}>${item.price}</div>
          <div className={classes.actions}>
            <IoMdCart className={classes.icon} />
            <MdDeleteForever className={classes.icon} />
          </div>
        </motion.div>
      ))}
    </div>
  );
};

export default Wishlist;
