import React,{useEffect, useState, useCallback} from "react";
import { RiHeart3Fill,RiShoppingCartFill } from "react-icons/ri";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

import classes from './Home.module.css';

const Home = () => {

  const [movieList, setMovieList]= useState([]);
  const  [toggleHeart, setToggleHeart] = useState(false);

  const settings = {
    dots: false,
    lazyLoad: true,
    infinite: true,
    speed: 500,
    slidesToShow: 5,
    slidesToScroll: 1
  };

  const getMovie = ()=>{
    fetch('https://api.themoviedb.org/3/discover/movie?api_key=d987bb3825166942aa314c4768160995')
    .then(res => res.json())
    .then(json => setMovieList(json.results));
  }

  const getColor = (vote) =>{
    if(vote >= 8)
      return 'green';
    else if(vote>=5 && vote<8)
      return 'orange';
    else
      return 'red';
  }

  useEffect(()=>{
    getMovie();
  },[]);

  const changeColor = useCallback(() =>{
    setToggleHeart(!toggleHeart)
   },[toggleHeart]);

  console.log(movieList);

  return (
    <div className={classes["container-carousel"]}>
      <div className={classes.overall}>
      <Slider {...settings}>  
      {
      movieList.map((movie)=>(
        <div className={classes.movie}>
          
          <img src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`} alt="" title={movie.title} className={classes.image} />

          <div className={classes["movie-info"]}>
            <h3 className={classes.title}>{movie.title}</h3> 
            <span className={classes[`${getColor(movie.vote_average)}`]}>{(movie.vote_average).toFixed(1)}</span>
          </div>

          <div className={classes.overview}>
            <div className={classes.text}>
              {movie.overview}
            </div>
            <span>
            <a href="https://google.com" >Know more</a>
            </span>
            <hr></hr>
            <button>
              <RiHeart3Fill size={24} className={toggleHeart ? `${classes["heart active"]}` : `${classes["heart"]}`} onClick={changeColor} />
            </button>
            <button>
              <RiShoppingCartFill size={24} color="black" />
            </button>
          </div>
          
        </div>
      )
    )
    }
    </Slider>
      </div>
    </div>
)
};

export default Home;
