import React, { useState } from "react";
import Slider from "react-slick";
import DetailsModal from "./DetailsModal";
import classes from "./Home.module.css";

function Carousel({ movieList, type, modalType }) {
  const [selectedMovie, setSelectedMovie] = useState({ movieId: null, modalType: null });

  const settings = {
    dots: false,
    lazyLoad: true,
    infinite: true,
    speed: 500,
    slidesToShow: 5,
    slidesToScroll: 1,
  };

  const getColor = (vote) => {
    if (vote >= 8) return "green";
    else if (vote >= 5 && vote < 8) return "orange";
    else return "red";
  };

  const openModal = (event, movieId) => {
    event.preventDefault();
    setSelectedMovie({ movieId, modalType });
  };

  const closeModal = () => setSelectedMovie({ movieId: null, modalType: null });

  return (
    <>
      <DetailsModal
        movieId={selectedMovie.movieId}
        type={type}
        modalType={selectedMovie.modalType}
        onClose={closeModal}
      />
      <div className={classes["container-carousel"]}>
        <Slider {...settings}>
          {movieList.map((movie) => (
            <div className={classes.movie} key={movie.id}>
              <img
                src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`}
                alt=""
                title={movie.title}
                className={classes.image}
              />
              <div className={classes["movie-info"]}>
                <h3 className={classes.title}>{movie.title}</h3>
                <span className={classes[`${getColor(movie.vote_average)}`]}>
                  {(movie.vote_average).toFixed(1)}
                </span>
              </div>
              <button onClick={(event) => openModal(event, movie.id)}>Know More</button>
            </div>
          ))}
        </Slider>
      </div>
    </>
  );
}

export default Carousel;
